CREATE OR REPLACE TRIGGER SMES.IF_DATE_EDITOR_LOG_TR 
  AFTER INSERT OR DELETE OR UPDATE ON SMES.IF_DATE_EDITOR_T   FOR EACH ROW
DECLARE
    -- LOCAL VARIABLES HERE
    --VERSION HISTORY
    --2011年9月25日 谌湘临 解除解析提交时，回复解析数据缓冲区
    M_OPT_TYPE VARCHAR2(1);
BEGIN
    IF INSERTING THEN
        M_OPT_TYPE := 'I';
        INSERT INTO IF_DATE_EDITOR_LOG_T
            (LOG_ID, OPRT_TYPE, MES_FCTR_ID, BEG_TIME, END_TIME, CMT_TIME, CMT_ID, DATE_EDITOR)
        VALUES
            (S_IF_DATE_EDITOR_LOG_T.NEXTVAL, M_OPT_TYPE, :NEW.MES_FCTR_ID, :NEW.BEG_TIME, :NEW.END_TIME, :NEW.CMT_TIME, :NEW.CMT_ID, :NEW.DATE_EDITOR);
    ELSIF UPDATING THEN
        M_OPT_TYPE := 'U';
        INSERT INTO IF_DATE_EDITOR_LOG_T
            (LOG_ID, OPRT_TYPE, MES_FCTR_ID, BEG_TIME, END_TIME, CMT_TIME, CMT_ID, DATE_EDITOR)
        VALUES
            (S_IF_DATE_EDITOR_LOG_T.NEXTVAL, M_OPT_TYPE, :NEW.MES_FCTR_ID, :NEW.BEG_TIME, :NEW.END_TIME, :NEW.CMT_TIME, :NEW.CMT_ID, :NEW.DATE_EDITOR);
    ELSIF DELETING THEN
        M_OPT_TYPE := 'D';
        INSERT INTO IF_DATE_EDITOR_LOG_T
            (LOG_ID, OPRT_TYPE, MES_FCTR_ID, BEG_TIME, END_TIME, CMT_TIME, CMT_ID, DATE_EDITOR)
        VALUES
            (S_IF_DATE_EDITOR_LOG_T.NEXTVAL, M_OPT_TYPE, :OLD.MES_FCTR_ID, :OLD.BEG_TIME, :OLD.END_TIME, :OLD.CMT_TIME, :OLD.CMT_ID, :OLD.DATE_EDITOR);
        --解除解析提交时
        IF :OLD.DATE_EDITOR = 1 THEN
