END;
/


CREATE OR REPLACE TRIGGER SMES.IF_INSTRDATA_TR
    after insert or delete or update ON SMES.IF_INSTRDATA_T
    for each row
declare
  -- local variables here
  v_instrtype_id  number;
begin
  if INSERTING then
    insert into IF_INSTRDATA_IDX_T
      (INSTR_ID, READ_TIME)
