    --如果物料代码不为空则返回
    IF :NEW.MATNR IS NOT NULL THEN
        --RETURN;
        NULL;
    END IF;

    --物料代码为空则找到一个物料代码
    SELECT COUNT(*)
        INTO CNT
        FROM PM_MTRL_T M
     WHERE TRIM(M.MTRL_ALIAS) LIKE TRIM(:NEW.ARKTX);
    IF CNT = 0 THEN
        MTRL_CODE := '未知代码';
    ELSE
        SELECT MTRL_CODE
            INTO MTRL_CODE
            FROM PM_MTRL_T M
         WHERE TRIM(M.MTRL_ALIAS) LIKE TRIM(:NEW.ARKTX)
             AND ROWNUM < 2;
    END IF;

    --更新货单的物料代码
    UPDATE CM_DELIVER_ORDER_T O
         SET O.MATNR = MTRL_CODE
     WHERE O.DELIVERY_ID = :NEW.DELIVERY_ID;

END;
/


