CREATE OR REPLACE TRIGGER SMES.IP_ROLES_T_TR 
  AFTER UPDATE or insert or delete ON SMES.IP_ROLES_T
  FOR EACH ROW
DECLARE
  -- LOCAL VARIABLES HERE
  --解决集成框架中如果修改了用户密码，自动同步到MES中，从而MES用户可以正常登陆系统
BEGIN
  IF INSERTING THEN

    INSERT INTO IP_ROLES_T_TRG
      SELECT S_PRI_SYNC_RECID.NEXTVAL,
             :NEW.role_id,
             :NEW.role_group_id,
             :NEW.role_name,
             :NEW.use_flag,
             :NEW.crt_date,
             :NEW.upd_date,
             :NEW.des,
             :NEW.display_order,
             'I1',
             SYSDATE,
             '0'
        FROM DUAL;

  END IF;

  IF UPDATING THEN

    INSERT INTO IP_ROLES_T_TRG
      SELECT S_PRI_SYNC_RECID.NEXTVAL,
             :OLD.role_id,
             :OLD.role_group_id,
             :OLD.role_name,
             :OLD.use_flag,
             :OLD.crt_date,
             :OLD.upd_date,
             :OLD.des,
             :OLD.display_order,
             'U0',
             SYSDATE,
             '0'
        FROM DUAL;

    INSERT INTO IP_ROLES_T_TRG
      SELECT S_PRI_SYNC_RECID.NEXTVAL,
             :NEW.role_id,
             :NEW.role_group_id,
             :NEW.role_name,
             :NEW.use_flag,
             :NEW.crt_date,
             :NEW.upd_date,
             :NEW.des,
             :NEW.display_order,
             'U1',
             SYSDATE,
             '0'
        FROM DUAL;


  END IF;

  IF DELETING THEN

    INSERT INTO IP_ROLES_T_TRG
      SELECT S_PRI_SYNC_RECID.NEXTVAL,
             :OLD.role_id,
             :OLD.role_group_id,
             :OLD.role_name,
             :OLD.use_flag,
             :OLD.crt_date,
             :OLD.upd_date,
             :OLD.des,
             :OLD.display_order,
             'D0',
             SYSDATE,
             '0'
        FROM DUAL;

  END IF;
EXCEPTION
  WHEN OTHERS THEN
    NULL;
END IP_ROLES_T_TR;
/


CREATE OR REPLACE TRIGGER SMES.OI_INSTR_ITEM_EX_TR 
  AFTER INSERT OR DELETE ON SMES.OI_INSTR_ITEM_T
  FOR EACH ROW
DECLARE
  v_instr_mode number;
 v_instr_id number;
BEGIN
--判断指令
    IF INSERTING THEN
      v_instr_id:=:NEW.instr_id;
    ELSIF DELETING THEN
      v_instr_id:=:OLD.instr_id;
    END IF;
  select instr.instr_mode
    into v_instr_mode
    from oi_instruction_t instr
   where v_instr_id = instr.id;
  if v_instr_mode = 11 then
    IF INSERTING THEN
      INSERT INTO oi_instr_item_extended_param_t
        (ID, INSTR_ITEM_ID, PTRL_MAX_VAR_RATE)
      VALUES
        (s_oi_instr_extended_param_t.nextval, :NEW.id, 5);
    ELSIF DELETING THEN
      delete from oi_instr_item_extended_param_t
       where INSTR_ITEM_ID = :OLD.id;
    END IF;
  end if;
exception
  when no_data_found then
    null;
END;
/


