CREATE OR REPLACE TRIGGER SMES.TRG_MEASDTL 
  AFTER INSERT OR DELETE OR UPDATE ON SMES.CM_MEAS_DTL_T  
  FOR EACH ROW
DECLARE
  -- LOCAL VARIABLES HERE
  TICKET_VAL NUMBER;
  MEAS_VAL   NUMBER;
  --MEAS_COUNT NUMBER;
  TICKET_ID VARCHAR2(200);
BEGIN

  --如果不是槽车计量则不做处理
  IF :OLD.MEAS_TYPE_ID = 'C' OR :NEW.MEAS_TYPE_ID = 'C' THEN
    NULL;
  ELSE
    RETURN;
  END IF;

  --获取当前计量票号
  IF DELETING THEN
        ----- 2010-10-19 燕山现场 -----
        --更新计量缓冲的使用标识为未用
        UPDATE CM_MEAS_SCALE_T SCL SET VAL15 = 0 WHERE SCL.REC_ID = :OLD.TEXT13;
        
    TICKET_ID := :OLD.MEAS_TICKET_ID;
    IF TICKET_ID IS NULL THEN
      --计量票为空，此时异常情况
      -- TO DO EXCEPTION
      INSERT INTO CM_MEAS_DTL_TRG
        (OPT_TIME, OPT_MONTH, OPT_USER, OPT_DES)
      VALUES
        (SYSDATE,
         TO_CHAR(SYSDATE, 'MM'),
         UTL_INADDR.GET_HOST_ADDRESS,
         '删除时当前计量票为空，货单：' || :OLD.MEAS_ORDER_ID);
    
      RETURN;
    END IF;
  
  ELSIF UPDATING THEN
    IF :OLD.MEAS_TICKET_ID <> :NEW.MEAS_TICKET_ID THEN
      --修改了计量记录的计量票号，此时异常情况
      -- TO DO  EXCEPTION
      INSERT INTO CM_MEAS_DTL_TRG
        (OPT_TIME, OPT_MONTH, OPT_USER, OPT_DES)
      VALUES
        (SYSDATE,
         TO_CHAR(SYSDATE, 'MM'),
         UTL_INADDR.GET_HOST_ADDRESS,
         '更新操作 计量记录的计量票号发生变更：(旧)' || :OLD.MEAS_TICKET_ID || '(新)' ||
         :NEW.MEAS_TICKET_ID);
    
      RETURN;
    ELSE
      TICKET_ID := :OLD.MEAS_TICKET_ID;
      IF TICKET_ID IS NULL THEN
        --计量票为空，此时异常情况
        -- TO DO EXCEPTION
        INSERT INTO CM_MEAS_DTL_TRG
          (OPT_TIME, OPT_MONTH, OPT_USER, OPT_DES)
        VALUES
          (SYSDATE,
           TO_CHAR(SYSDATE, 'MM'),
           UTL_INADDR.GET_HOST_ADDRESS,
           '更新时当前计量票为空，货单：' || :OLD.MEAS_ORDER_ID);
      
        RETURN;
      END IF;
    
    END IF;
  ELSIF INSERTING THEN
    
    ----- 2010-10-19 燕山现场 -----
        --更新计量缓冲的使用标识为在用
        UPDATE CM_MEAS_SCALE_T SCL SET VAL15 = 1 WHERE SCL.REC_ID = :NEW.TEXT13;
        
     
    TICKET_ID := :NEW.MEAS_TICKET_ID;
    IF TICKET_ID IS NULL THEN
      --计量票为空，此时异常情况
      -- TO DO EXCEPTION
      INSERT INTO CM_MEAS_DTL_TRG
        (OPT_TIME, OPT_MONTH, OPT_USER, OPT_DES)
      VALUES
        (SYSDATE,
         TO_CHAR(SYSDATE, 'MM'),
         UTL_INADDR.GET_HOST_ADDRESS,
         '插入时当前计量票为空，货单：' || :OLD.MEAS_ORDER_ID);
    
      RETURN;
    END IF;
  
  END IF;

  --当前计量票量
  BEGIN
    SELECT MEAS_TICKET_VAL
      INTO TICKET_VAL
      FROM CM_MEAS_TICKET_T
     WHERE MEAS_TICKET_ID = TICKET_ID;
  EXCEPTION
    WHEN OTHERS THEN
      --没有查到计量票，此时为异常情况
      -- TO DO EXCEPTION
      INSERT INTO CM_MEAS_DTL_TRG
        (OPT_TIME, OPT_MONTH, OPT_USER, OPT_DES)
      VALUES
        (SYSDATE,
         TO_CHAR(SYSDATE, 'MM'),
         UTL_INADDR.GET_HOST_ADDRESS,
         '当前计量记录对应的的计量票不存在：' || TICKET_ID);
    
      RETURN;
  END;

  --当前计量记录的和量
  BEGIN
    SELECT SUM(VAL)
      INTO MEAS_VAL
      FROM CM_MEAS_DTL_T
     WHERE MEAS_TICKET_ID = TICKET_ID;
  EXCEPTION
    WHEN OTHERS THEN
      --没有查到计量记录，此时是异常情况
      -- TO DO EXCEPTION
      INSERT INTO CM_MEAS_DTL_TRG
        (OPT_TIME, OPT_MONTH, OPT_USER, OPT_DES)
      VALUES
        (SYSDATE,
         TO_CHAR(SYSDATE, 'MM'),
         UTL_INADDR.GET_HOST_ADDRESS,
         '当前计量票的计量记录不存在:' || TICKET_ID);
    
      RETURN;
  END;

  IF TICKET_VAL <> MEAS_VAL THEN
    --计量票量与当前的计量记录的和量不相等，此为问题点
    -- TO DO WORK
  
    IF UPDATING THEN
      INSERT INTO CM_MEAS_DTL_TRG
        (REC_ID,
         MEAS_ORDER_ID,
         MEAS_TICKET_ID,
         OPT_TIME,
         OPT_MONTH,
         OPT_USER,
         OPT_DES)
      VALUES
        (:OLD.REC_ID,
         :OLD.MEAS_ORDER_ID,
         :OLD.MEAS_TICKET_ID,
         SYSDATE,
         TO_CHAR(SYSDATE, 'MM'),
         UTL_INADDR.GET_HOST_ADDRESS,
         '更新造成 当前计量票量不等于计量记录量（旧数据）');
      INSERT INTO CM_MEAS_DTL_TRG
        (REC_ID,
         MEAS_ORDER_ID,
         MEAS_TICKET_ID,
         OPT_TIME,
         OPT_MONTH,
         OPT_USER,
         OPT_DES)
      VALUES
        (:NEW.REC_ID,
         :NEW.MEAS_ORDER_ID,
         :NEW.MEAS_TICKET_ID,
         SYSDATE,
         TO_CHAR(SYSDATE, 'MM'),
         UTL_INADDR.GET_HOST_ADDRESS,
         '更新造成 当前计量票量不等于计量记录量（新数据）');
    
    ELSIF INSERTING THEN
      INSERT INTO CM_MEAS_DTL_TRG
        (REC_ID,
         MEAS_ORDER_ID,
         MEAS_TICKET_ID,
         OPT_TIME,
         OPT_MONTH,
         OPT_USER,
         OPT_DES)
      VALUES
        (:NEW.REC_ID,
         :NEW.MEAS_ORDER_ID,
         :NEW.MEAS_TICKET_ID,
         SYSDATE,
         TO_CHAR(SYSDATE, 'MM'),
         UTL_INADDR.GET_HOST_ADDRESS,
         '插入造成 当前计量票量不等于计量记录量');
    
    ELSIF DELETING THEN
      INSERT INTO CM_MEAS_DTL_TRG
        (REC_ID,
         MEAS_ORDER_ID,
         MEAS_TICKET_ID,
         OPT_TIME,
         OPT_MONTH,
         OPT_USER,
         OPT_DES)
      VALUES
        (:OLD.REC_ID,
         :OLD.MEAS_ORDER_ID,
         :OLD.MEAS_TICKET_ID,
         SYSDATE,
         TO_CHAR(SYSDATE, 'MM'),
         UTL_INADDR.GET_HOST_ADDRESS,
         '删除造成 当前计量票量不等于计量记录量');
    END IF;
  
  END IF;
EXCEPTION
  WHEN OTHERS THEN
    INSERT INTO CM_MEAS_DTL_TRG
      (OPT_TIME, OPT_MONTH, OPT_USER, OPT_DES)
    VALUES
      (SYSDATE,
       TO_CHAR(SYSDATE, 'MM'),
       UTL_INADDR.GET_HOST_ADDRESS,
       '触发器异常');
    RETURN;
  
END TRG_MEASDTL;
/


CREATE OR REPLACE TRIGGER SMES.TRG_MTRLINVT 
  AFTER UPDATE OR INSERT OR DELETE ON SMES.MB_MTRLINVTDATA_T
  FOR EACH ROW
declare
    -- LOCAL VARIABLES HERE
    --2012年04月17日 LIWB 增字段列表 
    --2010年10月1日 谌湘临 只保留非零的数据和有变化的数据，以降低数据量
    --2010年10月1日 谌湘临 操作类型：1-增；2-删；3-改，其中30为更新前，31为更新后
begin
    if UPDATING then
        --有变化才保存历史记录
        if :OLD.PLANTRAWVAL <> :NEW.PLANTRAWVAL
           or :OLD.PLANTVADVAL <> :NEW.PLANTVADVAL
           or :OLD.MESFCTRVADVAL <> :NEW.MESFCTRVADVAL
           or :OLD.MESFCTRAFMVAL <> :NEW.MESFCTRAFMVAL then
            insert into MB_MTRLINVTDATA_T_TRG
                (ID, MTRL_TANK_ID, CURTIME, BATCH, RANKID, PLANTRAWVAL, PLANTVADVAL, MESFCTRVADVAL, MESFCTRAFMVAL, OPT_TIME, OPT_ID)
            values
                (:OLD.ID, :OLD.MTRL_TANK_ID, :OLD.CURTIME, :OLD.BATCH, :OLD.RANKID, :OLD.PLANTRAWVAL, :OLD.PLANTVADVAL, :OLD.MESFCTRVADVAL, :OLD.MESFCTRAFMVAL, sysdate, 30);
            insert into MB_MTRLINVTDATA_T_TRG
                (ID, MTRL_TANK_ID, CURTIME, BATCH, RANKID, PLANTRAWVAL, PLANTVADVAL, MESFCTRVADVAL, MESFCTRAFMVAL, OPT_TIME, OPT_ID)
            values
                (:NEW.ID, :NEW.MTRL_TANK_ID, :NEW.CURTIME, :NEW.BATCH, :NEW.RANKID, :NEW.PLANTRAWVAL, :NEW.PLANTVADVAL, :NEW.MESFCTRVADVAL, :NEW.MESFCTRAFMVAL, sysdate, 31);
        end if;
    end if;
    if DELETING then
        --删除，全记录历史记录
        insert into MB_MTRLINVTDATA_T_TRG
            (ID, MTRL_TANK_ID, CURTIME, BATCH, RANKID, PLANTRAWVAL, PLANTVADVAL, MESFCTRVADVAL, MESFCTRAFMVAL, OPT_TIME, OPT_ID)
        values
            (:OLD.ID, :OLD.MTRL_TANK_ID, :OLD.CURTIME, :OLD.BATCH, :OLD.RANKID, :OLD.PLANTRAWVAL, :OLD.PLANTVADVAL, :OLD.MESFCTRVADVAL, :OLD.MESFCTRAFMVAL, sysdate, 2);
    
    end if;
    if INSERTING then
        --非全零，才保存历史记录
        if :NEW.PLANTRAWVAL <> 0
           or :NEW.PLANTVADVAL <> 0
           or :NEW.MESFCTRVADVAL <> 0
           or :NEW.MESFCTRAFMVAL <> 0 then
            insert into MB_MTRLINVTDATA_T_TRG
                (ID, MTRL_TANK_ID, CURTIME, BATCH, RANKID, PLANTRAWVAL, PLANTVADVAL, MESFCTRVADVAL, MESFCTRAFMVAL, OPT_TIME, OPT_ID)
            values
                (:NEW.ID, :NEW.MTRL_TANK_ID, :NEW.CURTIME, :NEW.BATCH, :NEW.RANKID, :NEW.PLANTRAWVAL, :NEW.PLANTVADVAL, :NEW.MESFCTRVADVAL, :NEW.MESFCTRAFMVAL, sysdate, 1);
        end if;
    
    end if;
exception
    when others then
        null;
    
end TRG_MTRLINVT;
/


